void dgemm(int ta, int tb, int M, int N, int K, double alpha, const double *A,
           int lda, const double *B, int ldb, double beta, double *C, int ldc)
{
#if 1
   if (alpha == 1.0)
   {
      if (beta == 1.0)
         dgemm_a1b1(ta, tb, M, N, K, alpha, A, lda, B, ldb, beta, C, ldc);
      else if (beta == 0.0)
         dgemm_a1b0(ta, tb, M, N, K, alpha, A, lda, B, ldb, beta, C, ldc);
      else
         dgemm_a1bX(ta, tb, M, N, K, alpha, A, lda, B, ldb, beta, C, ldc);
   }
   else
   {
      if (beta == 1.0)
         dgemm_aXb1(ta, tb, M, N, K, alpha, A, lda, B, ldb, beta, C, ldc);
      else if (beta == 0.0)
         dgemm_aXb0(ta, tb, M, N, K, alpha, A, lda, B, ldb, beta, C, ldc);
      else
         dgemm_aXbX(ta, tb, M, N, K, alpha, A, lda, B, ldb, beta, C, ldc);
   }
#else
   int i, j, k;
   register double c00;
   for (j=0; j < N; j++)
   {
      for (i=0; i < M; i++)
      {
         c00 = 0.0;
         for (k=0; k < K; k++)
               c00 += alpha * A[k+i*lda] * B[k+j*ldb];
         if (beta == 0.0)
            C[i+j*ldc] = c00;
         else 
            C[i+j*ldc] = C[i+j*ldc]*beta + c00;
      }
   }
#endif
}
#endif
